SET NAMES UTF8;
#丢弃数据库
DROP DATABASE IF EXISTS dp;
#创建数据库
CREATE DATABASE dp CHARSET=UTF8;
#进入该数据库
USE dp;
#创建数据表
CREATE TABLE users(
    uid INT PRIMARY KEY AUTO_INCREMENT,
    uname VARCHAR(32),
    upwd VARCHAR(32),
    email VARCHAR(64),
    phone VARCHAR(16) 
);
#插入用户数据

CREATE TABLE products(
    id INT PRIMARY KEY AUTO_INCREMENT,
    pid INT,
    lid INT,
    title VARCHAR(128),
    pname VARCHAR(60),
    price DECIMAL(10,2),
    img_url VARCHAR(50),
    promise VARCHAR(128), #承诺
    onsale VARCHAR(88), #促销
    spec VARCHAR(64),   #规格
    color VARCHAR(10),
    memory VARCHAR(32), #内存
    p_count INT
);


























#模糊查询
CREATE TABLE products_list(
    id INT PRIMARY KEY AUTO_INCREMENT,
    lid INT,
    title VARCHAR(128),
    pname VARCHAR(60),
    price DECIMAL(10,2),
    img_url VARCHAR(50),
    conditions VARCHAR(128)
);



